
"use strict";

const URL = require("../config/urls");



class IOTAnalyticsResource {
    constructor(options) {
      this.apiClient = options.apiClient;
    }
  
    // POST /api/v1/iot/analytics/distribution
    analyticsDistribution(options, callback) {
      return this.apiClient._postRequest(URL.IOT_ANALYTICS_QUERY.DISTRIBUTION, options, callback);
    }
  
    // POST /api/v1/iot/analytics/gridDistribution
    gridDistribution(options, callback) {
      return this.apiClient._postRequest(URL.IOT_ANALYTICS_QUERY.GRID_DISTRIBUTION, options, callback);
    }
  }
  

  module.exports = IOTAnalyticsResource;
  