"use strict";

const URL = require("../config/urls");

class LocationAnalyticsResource {
    // GET /api/v1/location/analytics/densityAroundIndex
    getDensityAroundIndex(callback) {
      return this.apiClient._getRequest(URL.LOCATION_ANALYTICS_RESOURCE.DENSITY_AROUND_INDEX, callback);
    }
  
    // POST /api/v1/location/analytics/distribution
    postLocationAnalyticsDistribution(options, callback) {
      return this.apiClient._postRequest(URL.LOCATION_ANALYTICS_RESOURCE.DISTRIBUTION, options, callback);
    }
  
    // GET /api/v1/location/analytics/floorDensities
    getFloorDensities(callback) {
      return this.apiClient._getRequest(URL.LOCATION_ANALYTICS_RESOURCE.FLOOR_DENSITIES, callback);
    }
  }
  

  module.exports = LocationAnalyticsResource;
  