"use strict";

const URL = require("../config/urls");

class LocationAttributesResource {
    // GET /api/v1/locationAttributes
    getLocationAttributes(callback) {
      return this.apiClient._getRequest(URL.LOCATION_ATTRIBUTES_RESOURCE.LOCATION_ATTRIBUTES, callback);
    }
  
    // POST /api/v1/locationAttributes
    postLocationAttribute(options, callback) {
      return this.apiClient._postRequest(URL.LOCATION_ATTRIBUTES_RESOURCE.LOCATION_ATTRIBUTES, options, callback);
    }
  
    // DELETE /api/v1/locationAttributes/{id}
    deleteLocationAttributeById(attributeId, callback) {
      const formattedUrl = URL.LOCATION_ATTRIBUTES_RESOURCE.LOCATION_ATTRIBUTE_BY_ID.replace('{id}', attributeId);
      return this.apiClient._deleteRequest(formattedUrl, callback);
    }
  }
  

  module.exports = LocationAttributesResource;
  