"use strict";

const URL = require("../config/urls");

class UserInfoResource {
	
	constructor(options) {
		this.apiClient = options.apiClient;
		//console.log('Hierarchy object created -> ', this.apiClient.getToken());
	}
    
    // POST /api/v1/user-info
    predict(options, callback) {
      return this.apiClient._postRequest(URL.ML_RESOURCE.PREDICT, options, callback);
    }
    
  }
  
  module.exports = UserInfoResource;
  