"use strict";

const URL = require("../config/urls");

class NetworkElementExternalResource {
    // GET /api/v1/networkElements/location/{locationId}
    getNetworkElementsByLocation(locationId, callback) {
      const formattedUrl = URL.NETWORK_ELEMENT_EXTERNAL_RESOURCE.NETWORK_ELEMENTS_BY_LOCATION.replace('{locationId}', locationId);
      return this.apiClient._getRequest(formattedUrl, callback);
    }
  
    // PUT /api/v1/networkElements/update/accessPoints
    updateAccessPoints(options, callback) {
      return this.apiClient._putRequest(URL.NETWORK_ELEMENT_EXTERNAL_RESOURCE.UPDATE_ACCESS_POINTS, options, callback);
    }
  
    // PUT /api/v1/networkElements/update/cameras
    updateCameras(options, callback) {
      return this.apiClient._putRequest(URL.NETWORK_ELEMENT_EXTERNAL_RESOURCE.UPDATE_CAMERAS, options, callback);
    }
  }
  
  module.exports = NetworkElementExternalResource;
  