
"use strict";

const URL = require("../config/urls");



class QueueResource {
    constructor(options) {
      this.apiClient = options.apiClient;
    }
  
  	load(callback) {
      return this.apiClient._getRequest(URL.QUEUE.LOAD, callback);
    }
    
    distribution(options, callback) {
      return this.apiClient._postRequest(URL.QUEUE.DISTRIBUTION, options, callback);
    }
  
    maxWaitTime(options, callback) {
      return this.apiClient._postRequest(URL.QUEUE.MAX_WAITTIME, options, callback);
    }
  }
  

  module.exports = QueueResource;
  