"use strict";

const URL = require("../config/urls");

class ReportResource {
    // POST /api/v1/reports/csvReport/{reportName}
    generateCsvReport(reportName, callback) {
      const formattedUrl = URL.REPORT_RESOURCE.CSV_REPORT.replace('{reportName}', reportName);
      return this.apiClient._postRequest(formattedUrl, {}, callback);
    }
  
    // POST /api/v1/reports/csvReport/{reportName}/{queryName}
    generateCsvReportByQuery(reportName, queryName, callback) {
      const formattedUrl = URL.REPORT_RESOURCE.CSV_REPORT_BY_QUERY.replace('{reportName}', reportName).replace('{queryName}', queryName);
      return this.apiClient._postRequest(formattedUrl, {}, callback);
    }
  }
  

  module.exports = ReportResource;
  