"use strict";

const URL = require('../config/urls');

class SymphonictResources {

    constructor(options) {
        this.apiClient = options.apiClient;
    }

    getAllOpenSpaces(callback) {
        return this.apiClient._getRequest(URL.SYMPHONICT.ALL_OPENSPACES, callback);
    }

    getAllProjects(callback) {
        return this.apiClient._getRequest(URL.SYMPHONICT.ALL_PROJECTS, callback);
    }

    postChatMessages(messageData, callback) {
        return this.apiClient._postRequest(URL.SYMPHONICT.MESSAGES, messageData, callback);
    }

    getUserInfo(callback) {
        return this.apiClient._getRequest(URL.SYMPHONICT.USER_INFO, callback);
    }

    updateUserInfo(userData, callback) {
        return this.apiClient._putRequest(URL.SYMPHONICT.USER_INFO, userData, callback);
    }

    sendZoomMeetingInfo(meetingInfo, callback) {
        return this.apiClient._postRequest(URL.SYMPHONICT.ZOOM_MTG_INFO, meetingInfo, callback);
    }
}

module.exports = SymphonictResources;
  