"use strict";

const URL = require("../config/urls");

class UserInfoResource {
	
	constructor(options) {
		this.apiClient = options.apiClient;
		//console.log('Hierarchy object created -> ', this.apiClient.getToken());
	}
    
    // POST /api/v1/user-info
    createUserInfo(options, callback) {
      return this.apiClient._postRequest(URL.USER_INFO_RESOURCE.USER_INFO, options, callback);
    }
  
    searchUserInfo(callback) {
      return this.apiClient._getRequest(URL.USER_INFO_RESOURCE.USER_SEARCH, callback);
    }
    
  }
  
  module.exports = UserInfoResource;
  